// src/admin/ProductDescription.js
import React from 'react';
import { useParams } from 'react-router-dom';
import './ProductDescription.css';
import productImages from '../photos'; // Placeholder images for products

const ProductDescription = () => {
  const { productId } = useParams();

  return (
    <div className="product-description">
      <header className="admin-header">
        <div className="header-left">B.Planet</div>
        <div className="header-right">
          <a href="/contact" className="header-link">Contact Us</a>
          <a href="/" className="header-link">Login</a>
          <img src={productImages.profile} alt="Profile" className="profile-image" />
        </div>
      </header>
      <div className="product-content">
        <div className="product-image">
          <img src={productImages[`product${productId}.jpg`]} alt={`Product ${productId}`} />
        </div>
        <div className="product-details">
          <h1>Product {productId}</h1>
          <p>This is the description for Product {productId}. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nullam scelerisque leo vel urna vestibulum, nec vestibulum nisl dignissim.</p>
        </div>
      </div>
    </div>
  );
};

export default ProductDescription;
