// src/components/Login.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';
import loginImage from '../photos/login-image.jpg'; // Ensure this path matches your file location

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    if (email === 'admin@example.com' && password === 'admin123') {
      navigate('/admin/home');
    } else {
      navigate('/user/home');
    }
  };

  return (
    <div className="login-page">
      <div className="login-header">
        <div className="header-left">B.Planet</div>
        <div className="header-right"><a href="/contact">Contact Us</a></div>
      </div>
      <div className="login-container">
        <div className="login-content">
          <div className="login-image">
            <img src={loginImage} alt="Description" />
          </div>
          <div className="login-form">
            <h2>Welcome back !!</h2>
            <form onSubmit={handleLogin}>
              <div>
                <label>Email</label>
                <input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder="abc@gmail.com"
                  required
                />
              </div>
              <div>
                <label>Password</label>
                <input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="******************"
                  required
                />
              </div>
              <div className="login-links">
                <a href="/forgot-password">Forget Password?</a>
                <a href="/sign-up">Sign up for free</a>
              </div>
              <button type="submit">Login</button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
