// src/admin/ProductDescription.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './ProductDescription.css';
import pencilIcon from '../photos/pencil.png'; // Ensure this path matches your file location

const products = [
  { id: 1, name: 'Product 1', description: 'Description for Product 1' },
  { id: 2, name: 'Product 2', description: 'Description for Product 2' },
  { id: 3, name: 'Product 3', description: 'Description for Product 3' },
  { id: 4, name: 'Product 4', description: 'Description for Product 4' },
  { id: 5, name: 'Product 5', description: 'Description for Product 5' },
  { id: 6, name: 'Product 6', description: 'Description for Product 6' },
  { id: 7, name: 'Product 7', description: 'Description for Product 7' },
  { id: 8, name: 'Product 8', description: 'Description for Product 8' },
  { id: 9, name: 'Product 9', description: 'Description for Product 9' },
  { id: 10, name: 'Product 10', description: 'Description for Product 10' },
  { id: 11, name: 'Product 11', description: 'Description for Product 11' },
  { id: 12, name: 'Product 12', description: 'Description for Product 12' },
  { id: 13, name: 'Product 13', description: 'Description for Product 13' },
  { id: 14, name: 'Product 14', description: 'Description for Product 14' },
  { id: 15, name: 'Product 15', description: 'Description for Product 15' },
  { id: 16, name: 'Product 16', description: 'Description for Product 16' },
  { id: 17, name: 'Product 17', description: 'Description for Product 17' },
  { id: 18, name: 'Product 18', description: 'Description for Product 18' },
  { id: 19, name: 'Product 19', description: 'Description for Product 19' },
  { id: 20, name: 'Product 20', description: 'Description for Product 20' },
];

const ProductDescription = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [editedDescription, setEditedDescription] = useState('');

  useEffect(() => {
    const product = products.find(p => p.id === parseInt(id));
    if (product) {
      setProduct(product);
      setEditedDescription(product.description);
    }
  }, [id]);

  const handleEditClick = () => {
    setIsEditing(true);
  };

  const handleSaveClick = () => {
    const updatedProducts = products.map(p =>
      p.id === product.id ? { ...p, description: editedDescription } : p
    );
    setProduct(updatedProducts.find(p => p.id === product.id));
    setIsEditing(false);
  };

  if (!product) {
    return <div>Product not found</div>;
  }

  return (
    <div className="product-description">
      <div className="product-header">
        <h1>{product.name}</h1>
        <div className="product-header-details">
          <span>Product No: {product.id}</span>
          <img
            src={pencilIcon}
            alt="Edit"
            className="edit-icon"
            onClick={handleEditClick}
          />
        </div>
      </div>
      {isEditing ? (
        <div className="product-edit">
          <textarea
            value={editedDescription}
            onChange={(e) => setEditedDescription(e.target.value)}
            rows="10"
          />
          <button onClick={handleSaveClick}>Save</button>
        </div>
      ) : (
        <div className="product-body">
          <p>{product.description}</p>
        </div>
      )}
      <button onClick={() => navigate('/admin')}>Back to Home</button>
    </div>
  );
};

export default ProductDescription;
